mapper.select:
select

from gld_accounting_entity gae


        and gae.ACC_ENTITY_ID = #{accEntityId,jdbcType = INTEGER}


        and gae.ACC_ENTITY_CODE = #{accEntityCode,jdbcType = VARCHAR}


        and gae.ACC_ENTITY_NAME = #{accEntityName,jdbcType = VARCHAR}


        and gae.FUNCTIONAL_CURRENCY_CODE = #{functionalCurrencyCode,jdbcType = VARCHAR}


        and gae.PAY_CURRENCY_CODE = #{payCurrencyCode,jdbcType = VARCHAR}


        and gae.COMPANY_TYPE = #{companyType,jdbcType = VARCHAR}


        and gae.START_DATE_ACTIVE = #{startDateActive,jdbcType = TIMESTAMP}


        and gae.END_DATE_ACTIVE = #{endDateActive,jdbcType = TIMESTAMP}


        and gae.DEFAULT_SET_OF_BOOKS_ID = #{defaultSetOfBooksId,jdbcType = INTEGER}


        and gae.DEFAULT_TIMEZONE_ID = #{defaultTimezoneId,jdbcType = INTEGER}


        and gae.DEFAULT_LANGUAGE = #{defaultLanguage,jdbcType = VARCHAR}


        and gae.DEFAULT_COUNTRY_CODE = #{defaultCountryCode,jdbcType = VARCHAR}


        and gae.TAXPAYER_TYPE = #{taxpayerType,jdbcType = VARCHAR}


        and gae.TAXPAYER_NUMBER = #{taxpayerNumber,jdbcType = VARCHAR}


        and gae.PHONE = #{phone,jdbcType = VARCHAR}


        and gae.ADDRESS = #{address,jdbcType = VARCHAR}


        and gae.BANK_ACCOUNT = #{bankAccount,jdbcType = VARCHAR}


        and gae.TENANT_ID = #{tenantId,jdbcType = INTEGER}


        and gae.district_code = #{districtCode,jdbcType = VARCHAR}


        and gae.company_nature_code = #{companyNatureCode,jdbcType = VARCHAR}


        and gae.factory_code = #{factoryCode,jdbcType = VARCHAR}
