mapper.select:
select

from fnd_company fc


        and fc.COMPANY_ID = #{companyId,jdbcType = INTEGER}


        and fc.COMPANY_CODE = #{companyCode,jdbcType = VARCHAR}


        and fc.COMPANY_SHORT_NAME = #{companyShortName,jdbcType = VARCHAR}


        and fc.COMPANY_FULL_NAME = #{companyFullName,jdbcType = VARCHAR}


        and fc.MAG_ORG_ID = #{magOrgId,jdbcType = INTEGER}


        and fc.ADDRESS = #{address,jdbcType = VARCHAR}


        and fc.COMPANY_LEVEL_ID = #{companyLevelId,jdbcType = INTEGER}


        and fc.PARENT_COMPANY_ID = #{parentCompanyId,jdbcType = INTEGER}


        and fc.SYSTEM_TIMEZONE_ID = #{systemTimezoneId,jdbcType = INTEGER}


        and fc.LANGUAGE = #{language,jdbcType = VARCHAR}


        and fc.MANAGING_CURRENCY = #{managingCurrency,jdbcType = VARCHAR}


        and fc.CHIEF_POSITION_ID = #{chiefPositionId,jdbcType = INTEGER}


        and fc.START_DATE_ACTIVE = #{startDateActive,jdbcType = TIMESTAMP}


        and fc.END_DATE_ACTIVE = #{endDateActive,jdbcType = TIMESTAMP}


        and fc.COMPANY_INFO_URL = #{companyInfoUrl,jdbcType = VARCHAR}


        and fc.TENANT_ID = #{tenantId,jdbcType = INTEGER}
