mapper.select:
select

from exp_employee ee


        and ee.EMPLOYEE_ID = #{employeeId,jdbcType = INTEGER}


        and ee.EMPLOYEE_CODE = #{employeeCode,jdbcType = VARCHAR}


        and ee.NAME = #{name,jdbcType = VARCHAR}


        and ee.EMAIL = #{email,jdbcType = VARCHAR}


        and ee.MOBIL = #{mobil,jdbcType = VARCHAR}


        and ee.PHONE = #{phone,jdbcType = VARCHAR}


        and ee.BANK_OF_DEPOSIT = #{bankOfDeposit,jdbcType = VARCHAR}


        and ee.BANK_ACCOUNT = #{bankAccount,jdbcType = VARCHAR}


        and ee.ENABLED_FLAG = #{enabledFlag,jdbcType = VARCHAR}


        and ee.EMPLOYEE_TYPE_ID = #{employeeTypeId,jdbcType = INTEGER}


        and ee.ID_TYPE = #{idType,jdbcType = VARCHAR}


        and ee.ID_CODE = #{idCode,jdbcType = VARCHAR}


        and ee.NOTES = #{notes,jdbcType = VARCHAR}


        and ee.NATIONAL_IDENTIFIER = #{nationalIdentifier,jdbcType = VARCHAR}


        and ee.HMAP_SYNC_FLAG = #{hmapSyncFlag,jdbcType = VARCHAR}


        and ee.HMAP_SYNC_DATE = #{hmapSyncDate,jdbcType = TIMESTAMP}


        and ee.PLACE_ID = #{placeId,jdbcType = INTEGER}


        and ee.TENANT_ID = #{tenantId,jdbcType = INTEGER}


        and ee.source_code = #{sourceCode,jdbcType = VARCHAR}


        and ee.source_header_id = #{sourceHeaderId,jdbcType = INTEGER}


        and ee.source_line_id = #{sourceLineId,jdbcType = INTEGER}


        and ee.source_reference = #{sourceReference,jdbcType = VARCHAR}
