        and hil3.line_number = #{lineNumber,jdbcType = VARCHAR}


        and hil3.description = #{description,jdbcType = VARCHAR}


        and hil3.pay_flag = #{payFlag,jdbcType = VARCHAR}


        and hil3.invoice_method = #{invoiceMethod,jdbcType = VARCHAR}


        and hil3.amount = #{amount,jdbcType = OTHER}


        and hil3.invoice_date = #{invoiceDate,jdbcType = TIMESTAMP}


        and hil3.TENANT_ID = #{tenantId,jdbcType = INTEGER}
