mapper.select:
select

from acp_mo_invoice_type amit


        and amit.MO_INVOICE_TYPE_ID = #{moInvoiceTypeId,jdbcType = INTEGER}


        and amit.MAG_ORG_ID = #{magOrgId,jdbcType = INTEGER}


        and amit.MO_INVOICE_TYPE_CODE = #{moInvoiceTypeCode,jdbcType = VARCHAR}


        and amit.DESCRIPTION = #{description,jdbcType = VARCHAR}


        and amit.CURRENCY_CODE = #{currencyCode,jdbcType = VARCHAR}


        and amit.INVOICE_TYPE = #{invoiceType,jdbcType = VARCHAR}


        and amit.PAGE_TEMPLATE = #{pageTemplate,jdbcType = VARCHAR}


        and amit.SOURCE_TYPE = #{sourceType,jdbcType = VARCHAR}


        and amit.JE_ACC_TEMPLATE = #{jeAccTemplate,jdbcType = VARCHAR}


        and amit.REPORT_NAME = #{reportName,jdbcType = VARCHAR}


        and amit.PAYEE_CATEGORY = #{payeeCategory,jdbcType = VARCHAR}


        and amit.PAYMENT_METHOD_ID = #{paymentMethodId,jdbcType = INTEGER}


        and amit.DIFFERENCE_FLAG = #{differenceFlag,jdbcType = VARCHAR}


        and amit.DIFFERENCE_CODE = #{differenceCode,jdbcType = VARCHAR}


        and amit.POSITIVE_DIFFERENCE = #{positiveDifference,jdbcType = OTHER}


        and amit.NEGATIVE_DIFFERENCE = #{negativeDifference,jdbcType = OTHER}


        and amit.INVOICE_GROUP_FLAG = #{invoiceGroupFlag,jdbcType = VARCHAR}


        and amit.INVESTMENT_PLAN_CONTROL = #{investmentPlanControl,jdbcType = VARCHAR}


        and amit.AUTO_APPROVE_FLAG = #{autoApproveFlag,jdbcType = VARCHAR}


        and amit.AUTO_AUDIT_FLAG = #{autoAuditFlag,jdbcType = VARCHAR}


        and amit.ADJUSTMENT_FLAG = #{adjustmentFlag,jdbcType = VARCHAR}


        and amit.PAYMENT_FLAG = #{paymentFlag,jdbcType = VARCHAR}


        and amit.FROZEN_FLAG = #{frozenFlag,jdbcType = VARCHAR}


        and amit.RESERVE_BUDGET = #{reserveBudget,jdbcType = VARCHAR}


        and amit.BUDGET_CONTROL_ENABLED = #{budgetControlEnabled,jdbcType = VARCHAR}


        and amit.ICON = #{icon,jdbcType = VARCHAR}


        and amit.CAPTION_HDS_ID = #{captionHdsId,jdbcType = INTEGER}


        and amit.ENABLED_FLAG = #{enabledFlag,jdbcType = VARCHAR}


        and amit.BUSINESS_CATEGORY = #{businessCategory,jdbcType = VARCHAR}


        and amit.TEMPORARY_MODEL = #{temporaryModel,jdbcType = VARCHAR}


        and amit.REVERSE_PLAN = #{reversePlan,jdbcType = VARCHAR}


        and amit.TEMPORARY_TAX = #{temporaryTax,jdbcType = VARCHAR}


        and amit.REQUEST_ID = #{requestId,jdbcType = INTEGER}


        and amit.PROGRAM_ID = #{programId,jdbcType = INTEGER}


        and amit.LAST_UPDATE_LOGIN = #{lastUpdateLogin,jdbcType = INTEGER}


        and amit.DOC_SCAN_FLAG = #{docScanFlag,jdbcType = VARCHAR}


        and amit.tenant_id = #{tenantId,jdbcType = INTEGER}
