mapper.select:
select

from exp_org_unit eou


        and eou.UNIT_ID = #{unitId,jdbcType = INTEGER}


        and eou.COMPANY_ID = #{companyId,jdbcType = INTEGER}


        and eou.UNIT_CODE = #{unitCode,jdbcType = VARCHAR}


        and eou.UNIT_TYPE_ID = #{unitTypeId,jdbcType = INTEGER}


        and eou.DESCRIPTION = #{description,jdbcType = VARCHAR}


        and eou.PARENT_UNIT_ID = #{parentUnitId,jdbcType = INTEGER}


        and eou.CHIEF_POSITION_ID = #{chiefPositionId,jdbcType = INTEGER}


        and eou.ORG_UNIT_LEVEL_ID = #{orgUnitLevelId,jdbcType = INTEGER}


        and eou.ENABLED_FLAG = #{enabledFlag,jdbcType = VARCHAR}


        and eou.TENANT_ID = #{tenantId,jdbcType = INTEGER}


        and eou.source_code = #{sourceCode,jdbcType = VARCHAR}


        and eou.source_header_id = #{sourceHeaderId,jdbcType = INTEGER}


        and eou.source_line_id = #{sourceLineId,jdbcType = INTEGER}


        and eou.source_reference = #{sourceReference,jdbcType = VARCHAR}
