mapper.select:
select

from hfm_payment_line_37799 hpl3


        and hpl3.line_id = #{lineId,jdbcType = INTEGER}


        and hpl3.header_id = #{headerId,jdbcType = INTEGER}


        and hpl3.line_number = #{lineNumber,jdbcType = VARCHAR}


        and hpl3.description = #{description,jdbcType = VARCHAR}


        and hpl3.pay_flag = #{payFlag,jdbcType = VARCHAR}


        and hpl3.payment_method = #{paymentMethod,jdbcType = VARCHAR}


        and hpl3.amount = #{amount,jdbcType = OTHER}


        and hpl3.payment_date = #{paymentDate,jdbcType = TIMESTAMP}


        and hpl3.TENANT_ID = #{tenantId,jdbcType = INTEGER}
