mapper.select:
select

from csh_payment_method cpm


        and cpm.PAYMENT_METHOD_ID = #{paymentMethodId,jdbcType = INTEGER}


        and cpm.PAYMENT_METHOD_CODE = #{paymentMethodCode,jdbcType = VARCHAR}


        and cpm.DESCRIPTION = #{description,jdbcType = VARCHAR}


        and cpm.PAY_METHOD_CODE = #{payMethodCode,jdbcType = VARCHAR}


        and cpm.PAY_CARRIER_CODE = #{payCarrierCode,jdbcType = VARCHAR}


        and cpm.POSTING_FLAG = #{postingFlag,jdbcType = VARCHAR}


        and cpm.ENABLED_FLAG = #{enabledFlag,jdbcType = VARCHAR}


        and cpm.TENANT_ID = #{tenantId,jdbcType = INTEGER}
