mapper.select:
select

from hfm_transfer_header hth


        and hth.transfer_header_id = #{transferHeaderId,jdbcType = INTEGER}


        and hth.transfer_number = #{transferNumber,jdbcType = VARCHAR}


        and hth.company_id = #{companyId,jdbcType = INTEGER}


        and hth.unit_id = #{unitId,jdbcType = INTEGER}


        and hth.position_id = #{positionId,jdbcType = INTEGER}


        and hth.employee_id = #{employeeId,jdbcType = INTEGER}


        and hth.acc_entity_id = #{accEntityId,jdbcType = INTEGER}


        and hth.transfer_apply_date = #{transferApplyDate,jdbcType = OTHER}


        and hth.bank_treasurer_req_type_id = #{bankTreasurerReqTypeId,jdbcType = INTEGER}


        and hth.transfer_currency_code = #{transferCurrencyCode,jdbcType = VARCHAR}


        and hth.payment_method_id = #{paymentMethodId,jdbcType = INTEGER}


        and hth.transfer_status = #{transferStatus,jdbcType = VARCHAR}


        and hth.description = #{description,jdbcType = VARCHAR}


        and hth.transfer_close_date = #{transferCloseDate,jdbcType = OTHER}
