mapper.select:
select

from hfm_transfer_line htl


        and htl.transfer_line_id = #{transferLineId,jdbcType = INTEGER}


        and htl.transfer_header_id = #{transferHeaderId,jdbcType = INTEGER}


        and htl.line_number = #{lineNumber,jdbcType = INTEGER}


        and htl.transfer_acc_entity_id = #{transferAccEntityId,jdbcType = INTEGER}


        and htl.transfer_bank_account_num = #{transferBankAccountNum,jdbcType = VARCHAR}


        and htl.transfer_bank_account_name = #{transferBankAccountName,jdbcType = VARCHAR}


        and htl.transfer_bank_account_code = #{transferBankAccountCode,jdbcType = VARCHAR}


        and htl.transfer_money = #{transferMoney,jdbcType = OTHER}


        and htl.actual_transfer_money = #{actualTransferMoney,jdbcType = OTHER}


        and htl.transfer_cash_plan_item_id = #{transferCashPlanItemId,jdbcType = INTEGER}


        and htl.transfer_cash_flow_term_id = #{transferCashFlowTermId,jdbcType = INTEGER}


        and htl.retreat_acc_entity_id = #{retreatAccEntityId,jdbcType = INTEGER}


        and htl.retreat_bank_account_code = #{retreatBankAccountCode,jdbcType = VARCHAR}


        and htl.retreat_money = #{retreatMoney,jdbcType = OTHER}


        and htl.actual_retreat_money = #{actualRetreatMoney,jdbcType = OTHER}


        and htl.retreat_cash_plan_item_id = #{retreatCashPlanItemId,jdbcType = INTEGER}


        and htl.retreat_cash_flow_term_id = #{retreatCashFlowTermId,jdbcType = INTEGER}


        and htl.payment_method_id = #{paymentMethodId,jdbcType = INTEGER}


        and htl.payment_date = #{paymentDate,jdbcType = OTHER}


        and htl.payment_status = #{paymentStatus,jdbcType = VARCHAR}


        and htl.plan_transfer_date = #{planTransferDate,jdbcType = OTHER}


        and htl.close_status = #{closeStatus,jdbcType = VARCHAR}


        and htl.description = #{description,jdbcType = VARCHAR}
